sp_widget.client_script:
function($scope,$rootScope,spModal,spUtil) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote', function(event,data) {
		console.log('selectNote event listener started');
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID

		}).then(function(r) {
			console.log('selectNote promise received');
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
			console.log('selectNote promise processed');
		});
	});


	c.selectButton = function() {
		console.log('clicked');
		var obj = {};

		obj.noteID = c.data.noteID;
		obj.noteBody = c.data.note;
		obj.noteTitle = c.data.title;

		console.log('obj: ' + JSON.stringify(obj))
		console.log('c.data: ' + JSON.stringify(c));

		c.server.get({
			action: 'updateNote',
			noteID: obj.noteID,
			noteBody: obj.noteBody,
			noteTitle: obj.noteTitle

		}).then(function(r){

			console.log('r.data: ' + JSON.stringify(r.data));
			$scope.$emit('renderNote', r.data);

		})

	}

	c.deleteNote = function(){
		c.server.get({
			action: 'deleteNote',
			noteID: c.data.noteID			
		}).then(function(r){
			spUtil.addTrivialMessage('The ' + c.data.title + ' record has been deleted');
			$rootScope.$emit('deleteNote', c.data.noteID);
			c.data.title = '';
			c.data.note = '';
			c.data.noteID = '';
		})
	}


	c.confirmDelete = function(){
		spModal.confirm('Are you sure you want to delete this note record?').then(function(confirmed){
			if(confirmed){
				c.deleteNote();
			}
		});
	}


}

sp_widget.css:
.button{
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  margin: 4px 2px;	
  cursor: pointer;
}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
			}

			else if (input.action == 'updateNote') {
				note.title = input.noteTitle; 
				note.note = input.noteBody; 
				note.update();

				data.title = note.getValue('title');
				data.note = note.getValue('note').slice(0,20);

				console.log('server script: data title: ' + data.title);

			}

			else if(input.action == 'deleteNote') {
				note.deleteRecord();
			}

		}
	}

})();
