sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote', function(event,data) {
		console.log('selectNote event listener started');
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID

		}).then(function(r) {
			console.log('selectNote promise received');
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
			console.log('selectNote promise processed');
		});
	});


	c.selectButton = function(updateType){
			c.server.get({
				action: 'updateNote',
				noteID: c.data.noteID,
				noteBody: c.data.note,
				noteTitle: c.data.title
			}).then(function(r) {

				/*
				if(c.updateNote == 'title' || c.updateNote == 'body') {
					c.data.title = r.data.title;
					c.data.note = r.data.note;
				}
				*/

				var id = c.data.noteID;
				$rootScope.$emit('updateNote', id);
				console.log('id: ' + id);
				console.log('c.data.note: ' + c.data.note);

			});		
	}

}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
			}

			else if (input.action == 'updateNote') {
				note.title = input.noteTitle; 
				note.note = input.noteBody; 
				note.update(); 



			}

		}
	}

})();
